import * as React from 'react';
import Box from '@mui/material/Box';
import Stack from '@mui/material/Stack';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import CardActionArea from '@mui/material/CardActionArea';
export default function BoxBasic() {
    return (
        <Box component="section" sx={{ p: 2, border: '1px dashed grey', height: "100vh" }}>
            <Stack direction="row" spacing={8} sx={{ justifyContent: 'space-evenly' }}>
                <Box component="section" sx={{ p: 2, maxWidth: '30%' }}>
                    <Typography variant='h2' sx={{ textAlign: 'center' }}>Engineering Dreams with Artificial Intelligence</Typography>
                    <Typography variant='h4' sx={{ textAlign: 'center', color: 'white' }}>
                        Welcome to the VIT Bhopal Universityâ€™s AI Club, where technology enthusiasts and innovators connect to explore the endless possibilities of artificial intelligence. Join us to collaborate, learn, and push the boundaries of what AI can achieve.
                    </Typography>
                </Box>

                <Card sx={{

                    height: "500px",

                    borderRadius: "50%", // Keeps it circular, adjust or remove for a different shape
                    overflow: "hidden",
                    boxShadow: "0px 0px 30px 15px rgba(128, 0, 128, 0.6)", // Initial soft glow
                    position: "relative",
                    "&::before": {
                        content: '""',
                        position: "absolute",
                        top: "50%",
                        left: "50%",
                        width: "200vw", // 200% of viewport width to extend the gradient glow
                        height: "200vh", // 200% of viewport height for full coverage
                        borderRadius: "50%",
                        background:
                            "radial-gradient(circle, rgba(128,0,128,0.8), rgba(128,0,128,0) 80%)",
                        transform: "translate(-50%, -50%) scale(2)", // Center the gradient and scale it
                        filter: "blur(80px)", // Heavy blur for a soft glow spread
                        zIndex: -1,
                    },
                }}>

                    <CardMedia
                        component="img"
                        height="500px"
                        image="image1.jpg"
                        title="bg image"
                    />

                </Card>
            </Stack>

        </Box>
    );
}
